export function pluralize(name, count) {
  if (count === 1) {
    return name
  }
  return name + 's'
}

export function idbPromise(storeName, method, object) {
  return new Promise((resolve, reject) => {
    // open connectio to the database 'shop-shop' with version of 1
    const requestion = window.indexedDB.open('shop-shop', 1)

  })
}
